// ===========================================================================//	LEventHandlerFunctor.h		PowerPlant 2.1			 ©2000 Metrowerks Inc.// ===========================================================================////	A Carbon Event handler where the Toolbox callback calls an ordinary//	C function#ifndef _H_LEventHandlerFunctor#define _H_LEventHandlerFunctor#pragma once#include <LEventHandler.h>PP_Begin_Namespace_PowerPlant// ---------------------------------------------------------------------------class	LEventHandlerFunctor : public LEventHandler {public:	typedef	 OSStatus (* HandlerFunc)(EventHandlerCallRef, EventRef);						LEventHandlerFunctor();										LEventHandlerFunctor(							EventTargetRef			inTarget,							UInt32					inNumTypes,							const EventTypeSpec*	inTypeList,							HandlerFunc				inFunc);					LEventHandlerFunctor(							EventTargetRef		inTarget,							EventClass			inEventClass,							EventType			inEventType,							HandlerFunc			inFunc);								virtual			~LEventHandlerFunctor();		void			SetEventTarget( EventTargetRef inTarget );		OSStatus		Install(							EventTargetRef			inTarget,							UInt32					inNumTypes,							const EventTypeSpec*	inTypeList,							HandlerFunc				inFunc);		OSStatus		Install(							EventTargetRef			inTarget,							EventClass				inEventClass,							EventType				inEventType,							HandlerFunc				inFunc);		HandlerFunc		GetHandlerFunc() const;		void			SetHandlerFunc( HandlerFunc inFunc );private:	static pascal	OSStatus		EventHandlerCallback(							EventHandlerCallRef	inCallRef,							EventRef			inEventRef,							void*				inUserData);private:	HandlerFunc		mHandlerFunc;		static EventHandlerUPP	sHandlerUPP;};// ===========================================================================//	Inline Functions// ===========================================================================inlineLEventHandlerFunctor::HandlerFuncLEventHandlerFunctor::GetHandlerFunc() const{	return mHandlerFunc;}inlinevoidLEventHandlerFunctor::SetHandlerFunc(	HandlerFunc	inFunc){	mHandlerFunc = inFunc;}PP_End_Namespace_PowerPlant#endif