// ===========================================================================//	LCFBoolean.h				PowerPlant 2.1			 ©2000 Metrowerks Inc.// ===========================================================================////	Wrapper class for Core Foundation Boolean data type#ifndef _H_LCFBoolean#define _H_LCFBoolean#pragma once#include <LCFObject.h>#include <CFNumber.h>// ---------------------------------------------------------------------------class	LCFBoolean : public LCFObject {public:						LCFBoolean( Boolean inValue );						LCFBoolean( const LCFBoolean& inSource );						LCFBoolean( CFBooleanRef inBooleanRef );	LCFBoolean&			operator = (const LCFBoolean& inSource );	virtual				~LCFBoolean();						operator CFBooleanRef() const;	CFBooleanRef		GetTypeRef() const;	Boolean				GetValue() const;};// ===========================================================================//	Inline Functions// ===========================================================================// ---------------------------------------------------------------------------//	¥ operator CFBooleanRef											  [public]// ---------------------------------------------------------------------------inlineLCFBoolean::operator CFBooleanRef() const{	return GetTypeRef();}// ---------------------------------------------------------------------------//	¥ GetTypeRef													  [public]// ---------------------------------------------------------------------------inlineCFBooleanRefLCFBoolean::GetTypeRef() const{	return reinterpret_cast<CFBooleanRef>( LCFObject::GetTypeRef() );}// ---------------------------------------------------------------------------//	¥ GetValue														  [public]// ---------------------------------------------------------------------------inlineBooleanLCFBoolean::GetValue() const{	return ::CFBooleanGetValue( GetTypeRef() );}#endif