ument,		kBundleIDUnicodeTextDocument,			kBundleIDUnicodeTextStationery,		kBundleIDUnicodeTextStationery,			kBundleIDRTFDocument,				kBundleIDRTFDocument,			kBundleIDRTFStationery,				kBundleIDRTFStationery		}	}} ;resource 'FREF' ( kBundleIDApplication ){	ftApplication,	kBundleIDApplication,	""} ;resource 'FREF' ( kBundleIDTextDocument ){	ftTextDocument,	kBundleIDTextDocument,	""} ;resource 'FREF' ( kBundleIDTextStationery ){	ftTextStationery,	kBundleIDTextStationery,	""} ;resource 'FREF' ( kBundleIDUnicodeTextDocument ){	ftUnicodeTextDocument,	kBundleIDUnicodeTextDocument,	""} ;resource 'FREF' ( kBundleIDUnicodeTextStationery ){	ftUnicodeTextStationery,	kBundleIDUnicodeTextStationery,	""} ;resource 'FREF' ( kBundleIDRTFDocument ){	ftRTFDocument,	kBundleIDRTFDocument,	""} ;resource 'FREF' ( kBundleIDRTFStationery ){	ftRTFStationery,	kBundleIDRTFStationery,	""} ;resource 'open' ( 128 ){	sigWASTE,	{		ftTextDocument,		ftUnicodeTextDocument,		ftRTFDocument	}} ;resource 'kind' ( 128 ){	sigWASTE,	verUS,	{		ftApplicationName, 			"WASTE Demo",		ftTextDocument,				"Text file",		ftUnicodeTextDocument,		"Unicode text file",		ftRTFDocument,				"Rich Text Format document"	}} ;resource 'vers' ( 1 ){	0x02, 0x10, alpha, 8,		//	2.1a8	verUS,	"2.1a8",	"2.1a8, \0xA9 1993-2002 Marco Piovanelli"} ;read 'plst' ( 0 ) "WEDemoInfo.plist" ;