able)	$"",//	color table: there are only two colors: black and a variable color	0x0,	0,	{		0,		0x0000, 0x0000, 0x0000,		//	color 0: the icon munging code changes this		1,		0x0000, 0x0000, 0x0000		//	color 1: black (for the border)	},//	pixmap data#if 1		//	icon with a black border	$"FFFF FF00 8000 0100 8000 0100 8000 0100"	$"8000 0100 8000 0100 8000 0100 8000 0100"	$"8000 0100 8000 0100 8000 0100 FFFF FF00"#else		//	solid icon with no border	$"0000 0000 0000 0000 0000 0000 0000 0000"	$"0000 0000 0000 0000 0000 0000 0000 0000"	$"0000 0000 0000 0000 0000 0000 0000 0000"#endif} ;